import { Component, OnInit } from '@angular/core';
import { Contact } from '../Contact';
// import createConnectionPool, {sql} from '@databases/mysql';
@Component({
  selector: 'app-checkout-form',
  templateUrl: './checkout-form.component.html',
  styleUrls: ['./checkout-form.component.css']
})
export class CheckoutFormComponent implements OnInit {
msg : string ="gmail.com"
contact = new Contact();
  constructor() { }

  ngOnInit(): void {
  }

  submit(form)
  {
    this.contact.firstname = form.firstName;
    this.contact.lastname = form.lastName; 
    this.contact.email = form.email;  

    window.alert("Cart is successfully Submitted")
console.log(form);
// async function run() {
//   // N.B. you will need to replace this connection
//   // string with the correct string for your database.
//   const db = createConnectionPool(
//     'mysql://root:Naresh@localhost:3306/practice',
//   );

//   const results = await db.query(sql`
//     SELECT 1 + 1 as result;
//   `);

//   console.log(results);
//   // => [{result: 2}]

//   await db.dispose();
// }

// run().catch((err) => {
//   console.error(err);
//   process.exit(1);
// });
  }
}
<div class="my-container">

<form #contactForm ="ngForm" (ngSubmit) ="submit(contactForm.value)">
    <div class="form-group">
        <label for="exampleInputEmail1">First Name</label>
      <input type="name" class="form-control" ngModel name='Firstname'id="firstName" aria-describedby="emailHelp" placeholder="Enter First name">

    </div>
    <div class="form-group">
        <label for="exampleInputEmail1">Last Name</label>
        <input type="name" class="form-control" ngModel name="lastname" id="lastname" aria-describedby="emailHelp" placeholder="Enter Last name">
        <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
      </div>
      <div class="form-group">
        <label for="exampleInputEmail1">Email address</label>
        <input type="email" [ngModel] = "msg" name= "email"  class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email">
        <small id="emailHelp" class="form-text text-muted">We'll never share your email with anyone else.</small>
      </div>
    <div class="form-group">
      <label for="exampleInputPassword1">Password</label>
      <input type="password" class="form-control" ngModel name="Password" id="exampleInputPassword1" placeholder="Password">
    </div>
    <div class="form-group form-check">
      <input type="checkbox" class="form-check-input" id="exampleCheck1">
      <label class="form-check-label" for="exampleCheck1">Check me out</label>
    </div>
    <button type="submit" class="btn btn-primary">Submit</button>
  </form>
</div>
